$mapRows = explode("\n", trim($inputMap));
$grid = array_map(function($row) {
    return str_split(trim($row));
}, $mapRows);

if (count($grid) != 100) {
    echo "Error: The map does not have the expected number of rows.\n";
    return; 
}

$counts = [
    'shark' => 0,
    'bigShipT' => 0,
    'bigShipB' => 0,
    'bigShipD' => 0,
    'bigShipI' => 0
];

$isCorrupted = false;

foreach ($grid as $rowIndex => $row) {
    for ($i = 0; $i < count($row); $i++) {
        $char = $row[$i];
        $nextChar = $row[$i + 1] ?? null;

        if ($char == 'K') {
            if ($nextChar == 'K') {
                $counts['shark']++;
                $i++; 
            } else {
                $isCorrupted = true;
                break 2; 
            }
        }

        foreach (['T', 'B', 'D', 'I'] as $shipChar) {
            if ($char == $shipChar) {
                if (isset($row[$i + 3]) && $row[$i + 1] == $shipChar && $row[$i + 2] == $shipChar && $row[$i + 3] == $shipChar) {
                    $counts['bigShip' . $shipChar]++;
                    $i += 3; 
                } else {
                    $isCorrupted = true;
                    break 3; 
                }
            }
        }
    }
}

if ($isCorrupted) {
    echo "Error: The map is corrupted.\n";
} else {
    foreach ($counts as $entity => $count) {
        echo "{$entity}: {$count}\n";
    }
}